    while (window.isOpen())
        {
            sf::Event event{};
            while (window.pollEvent(event))
            {
                if(event.type==sf::Event::Closed)
                    window.close();
                if(event.type==sf::Event::Resized){
                    sf::View temp=window.getView();
                    temp.setSize(window.getSize().x,window.getSize().y);
                    window.setView(temp);
                }

            }

            window.clear(sf::Color::Black);


            if(sf::Keyboard::isKeyPressed(sf::Keyboard::W))
            {
                 x = (int)(hero.getPosition().x)/32/SCALE;
                 y = (int)((hero.getPosition().y-1)/32/SCALE);

               if(level[120*y+x]==0){
                   std::cout<<x<<" "<<y<<std::endl;
                   hero.move(0,-hero.getSpeed());
               }
               else{
                   float offset=0;
                   while(1){
                       offset+= 0.01;
                       y= static_cast<int>((hero.getPosition().y+offset)/32/SCALE);
                       if(level[120*y+x]==0){
                           hero.move(0, offset);
                           break;
                       }
                   }

               }



               hero.setTextureRect(sf::IntRect(64*counterWalking,0,64,64));

            }else if(sf::Keyboard::isKeyPressed(sf::Keyboard::S))
            {
                 x = (int)(hero.getPosition().x)/32/SCALE;
                 y = (int)((hero.getPosition().y)/32/SCALE)+1;
                if(level[120*y+x]==0){

                    std::cout<<x<<" "<<y<<std::endl;
                    hero.move(0,hero.getSpeed());

                    }else{
                    float offset=0;
                    while(1){
                        offset+= 0.01;
                        y= static_cast<int>((hero.getPosition().y-offset)/32/SCALE);
                        if(level[120*y+x]==0){
                            hero.move(0, -offset);
                            break;
                        }
                    }

                }

                hero.setTextureRect(sf::IntRect(64*counterWalking,128,64,64));

            }else if(sf::Keyboard::isKeyPressed(sf::Keyboard::A))

            {
                 x = (int)((hero.getPosition().x-1)/32/SCALE);
                 y = (int)(hero.getPosition().y)/32/SCALE;
                if(level[120*y+x]==0){
                    std::cout<<x<<" "<<y<<std::endl;
                    hero.move(-hero.getSpeed(),0);
                }else{
                    float offset=0;
                    while(1){
                        offset+= 0.01;
                        x= static_cast<int>((hero.getPosition().x+offset)/32/SCALE);
                        if(level[120*y+x]==0){
                            hero.move(offset, 0);
                            break;
                        }
                    }

                }


                hero.setTextureRect(sf::IntRect(64*counterWalking,64,64,64));

            }else if(sf::Keyboard::isKeyPressed(sf::Keyboard::D))

            {
                x = (int)((hero.getPosition().x)/32/SCALE)+1;
                y = (int)(hero.getPosition().y)/32/SCALE;
                if(level[120*y+x]==0){
                    std::cout<<x<<" "<<y<<std::endl;
                    hero.move(hero.getSpeed(),0);
                }else{
                    float offset=0;
                    while(1){
                        offset+= 0.01;
                        x= static_cast<int>((hero.getPosition().x-offset)/32/SCALE);
                        if(level[120*y+x]==0){
                            hero.move(-offset, 0);
                            break;
                        }
                    }

                }
                hero.setTextureRect(sf::IntRect(64*counterWalking,192,64,64));
            }

            if(sf::Mouse::isButtonPressed(sf::Mouse::Left))
            {   //right attack
                if((sf::Mouse::getPosition().x>hero.getPosition().x)&&(sf::Mouse::getPosition().y >hero.getPosition().y-32)&&(sf::Mouse::getPosition().y< hero.getPosition().y+32*4))
                    for (int i = 0;  i<10 ; ++i)
                    {
                        hero.setTextureRect(sf::IntRect(64 * i, 64 * 7, 64, 64));
                        window.draw(map);
                        window.draw(hero);
                        window.display();
                    }
                //down attack
               if((sf::Mouse::getPosition().y>hero.getPosition().y)&&(sf::Mouse::getPosition().x> hero.getPosition().x-32)&&(sf::Mouse::getPosition().x< hero.getPosition().x+32*4))
                    for (int i = 0;  i<10 ; ++i)
                    {
                        hero.setTextureRect(sf::IntRect(64 * i, 64 * 6, 64, 64));
                        window.draw(map);
                        window.draw(hero);
                        window.display();
                    }
               // left attack
                if((sf::Mouse::getPosition().x<hero.getPosition().x)&&(sf::Mouse::getPosition().y> hero.getPosition().y-32)&&(sf::Mouse::getPosition().y< hero.getPosition().y+32*4))
                    for (int i = 0;  i<10 ; ++i)
                    {
                        hero.setTextureRect(sf::IntRect(64 * i, 64 * 5, 64, 64));
                        window.draw(map);
                        window.draw(hero);
                        window.display();
                    }
                if((sf::Mouse::getPosition().y<hero.getPosition().y)&&(sf::Mouse::getPosition().x> hero.getPosition().x-32)&&(sf::Mouse::getPosition().x< hero.getPosition().x+32*4))
                    for (int i = 0;  i<10 ; ++i)
                     {
                        hero.setTextureRect(sf::IntRect(64 * i, 64 * 4, 64, 64));
                        window.draw(map);
                        window.draw(hero);
                        window.display();
                     }
            //hero.isLegalAttack()
            }



            if(counterWalking ==8){
                counterWalking = 0;
            }else{
                counterWalking++;
            }

            position.x = hero.getPosition().x +20 - (3840.0/2);
            position.y = hero.getPosition().y +20 - (2160.0/2);

            if(position.x < 0)
                position.x =0;
            if (position.y < 0)
                position.y =0;
            view.reset(sf::FloatRect(position.x, position.y, 3840, 2160));


            window.setFramerateLimit(30);
            window.setView(view);
            window.draw(map);
            window.draw(hero);
            window.display();



        }
        return 0;
